      </div>

      <div className="name-area">{name}</div>
      <div className="image-area">{image}</div>

      <div className="description-area">{description}</div>

      <div></div>
      <div className="learn-more-area">
        <Button />
      </div>
      <div className="next-area">{next}</div>
    </LayoutStyled>
  );
}

export default Layout;
